function animateNumbers(){document.querySelectorAll(".number").forEach(e=>{let t=0,n=parseInt(e.dataset.target),r=Math.ceil(n/100);function a(){t+=r,t>n&&(t=n),e.textContent=t.toLocaleString(),t<n&&requestAnimationFrame(a)}a()})}
function roadmapAnimation(){const e=new IntersectionObserver(t=>{t.forEach(o=>{o.isIntersecting&&o.target.classList.add("visible")})},{threshold:.2});document.querySelectorAll(".milestone").forEach(t=>e.observe(t))}
document.addEventListener("DOMContentLoaded",()=>{animateNumbers(),roadmapAnimation()});