function animateNumbers(){
  document.querySelectorAll(".number").forEach(el=>{
    let target=parseInt(el.dataset.target),count=0,step=Math.ceil(target/120);
    function update(){count+=step;if(count>target)count=target;el.textContent=count.toLocaleString();if(count<target)requestAnimationFrame(update)}
    update()
  })
}
function scrollEffects(){
  const obs=new IntersectionObserver(entries=>{
    entries.forEach(e=>{
      if(e.isIntersecting){e.target.style.opacity=1;e.target.style.transform="translateY(0)"}
    })
  },{threshold:.1});
  document.querySelectorAll(".stat,.vision-art,.milestone").forEach(el=>{
    el.style.opacity=0;el.style.transform="translateY(40px)";obs.observe(el)
  })
}
document.addEventListener("DOMContentLoaded",()=>{
  animateNumbers();
  scrollEffects();
});