!function(){
 const e=document.querySelectorAll(".number");
 e.forEach(n=>{
   let t=0,r=parseInt(n.dataset.target),a=Math.ceil(r/120);
   (function i(){t+=a,t>r&&(t=r),n.textContent=t.toLocaleString(),t<r&&requestAnimationFrame(i)})()
 });

 // Scroll active link
 const sections=document.querySelectorAll("section[id]");
 const navLinks=document.querySelectorAll(".nav-link");
 function setActive(){let i=window.scrollY+200;sections.forEach(sec=>{
   let top=sec.offsetTop,height=sec.offsetHeight,id=sec.getAttribute("id");
   if(i>=top&&i<top+height){navLinks.forEach(l=>l.classList.remove("active"));
   document.querySelector('.nav-link[href="#'+id+'"]')?.classList.add("active")}})}
 window.addEventListener("scroll",setActive);
}();